import Signup from './components/signup';
import ForgotPasswod from './components/forgot_password';

class App extends Component {
	 constructor(props) {
		 super(props);
		 this.state = { 

			login : true,
			forgetPassword : false,

		  }
	 }
	 render() { 
		//  console.log(this.state);
		 return ( this.state.login?<Login onclick_button={()=>{this.setState({forgetPassword : true,submissionMessage : "",login : false})}} onclick_button2={()=>{this.setState({login : false,submissionMessage : ""})}}/>:!this.state.forgetPassword?<Signup onclick_button={()=>this.setState({login : true,submissionMessage : ""})}/>:<ForgotPasswod/>);
	}
 }

 export default App;
