import React, { Component } from 'react';
// import axios from "axios";
import Popup from "reactjs-popup";

class Newpassword extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            newpassword : '',
            confirmpassword : '',
            submissionMessage:''
        }
    }
    fuc(){
        this.state.newpassword === this.state.confirmpassword?this.setState({submissionMessage:"Password reset succesful"}):this.setState({submissionMessage:"Password did not match"})

    }
    render() { 
        return ( <Popup open={true} position="right center">
                <center>

            <div style={{display : "flex",justifyContent : "center",alignItems : "center",height}}>
                <div>

                    <input type="password"  value={this.state.newpassword} onChange={e=>{this.setState({newpassword : e.target.value})}} placeholder="Enter New Password" required/>
                    <br/><br/>
                    <input type="password" value={this.state.confirmpassword} onChange={e=>{this.setState({confirmpassword : e.target.value})}} placeholder="Confirm New Password" required/>
                    <br/><br/>
                    <h3>{this.state.submissionMessage}</h3>
                    <button style={{backgroundColor : "#42f5e9"}} onClick={()=>{
                        this.fuc()
                    }}>Reset Password</button>
                    </div>
            </div>
            </center>
      </Popup> );
    }
}

export default Newpassword;
