import React, { Component } from 'react';
// import axios from "axios";
// import Popup from "reactjs-popup";

class Newpassword extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            newpassword : '',
            confirmpassword : '',
            submissionMessage:''
        }
    }
    fuc(){
        this.state.newpassword === this.state.confirmpassword?this.setState({submissionMessage:"Password reset succesful"}):this.setState({submissionMessage:"Password did not match"})

    }
    render() { 
        return (    <div class="popup-overlay " style="position: fixed; top: 0px; bottom: 0px; left: 0px; right: 0px; background: rgba(0, 0, 0, 0.5);z-index: 999;">
        <div class="popup-content " style="position: fixed;bottom: 40%;right:36%; background: rgb(255, 255, 255); margin: auto; border: 1px solid rgb(187, 187, 187); padding: 5px;height: 300px;width: 300px;display: flex; justify-content: center; align-items: center;">
                <div>
                    <input type="password" placeholder="Enter New Password" required="" value=""/>
                    <br/><br/>
                    <input type="password" placeholder="Confirm New Password" required="" value=""/>
                    <br/><br/>
                    <h3></h3>
                    &emsp;&emsp;    
                    <button style="background-color: rgb(66, 245, 233);">Reset Password</button>
                </div>
        </div>
    </div>);
    }
}

export default Newpassword;
