import React,{Component} from 'react';
import './App.css';
import axios from "axios";
import Login from './components/login';
import Signup from './components/signup'

class App extends Component {
	 constructor(props) {
		 super(props);
		 this.state = { 

			username : "",
			password : "",
			email : "",
			phone : "",
			submissionMessage : "",
			login : true,
			forgetPassword : false,
			system_otp : "",
			user_otp : ""

		  }
	 }
	 render() { 
		 console.log(this.state);
		 return ( this.state.login?<Login state={this.state} onchange_email={e=>{this.setState({email : e.target.value})}} onchange_password={e=>{this.setState({password : e.target.value})}} onclick_button={()=>{	
			axios.post("http://login-signup-1.herokuapp.com/login",{email:this.props.state.email,password:this.props.state.password})
		   .then(e=>{
			   console.log(e);
			   this.setState({submissionMessage : e.data.msg||e.data.error[0].error})
		   })
		   this.setState({email : "",password : ""})}} onclick_button2={()=>{this.setState({forgetPassword : true,submissionMessage : "",login : false})}} onclick_button3={()=>{this.setState({login : false,submissionMessage : ""})}}/>:!this.state.forgetPassword?<Signup onclick/>:<div className="div1">
			<div style={{color : "purple"}}><h1>PassWord Reset</h1></div>
			<br/>
			<div>

				<img src="https://www.genymotion.com/wp-content/themes/genymobile/img/page-reset-password/genymotion-android-simulator-reset-password.svg" alt="Forget PassWord"/>

			</div>
			<br/><br/>
			<div>
				<input type="text" value={this.state.phone} onChange={e=>{this.setState({phone : e.target.value})}} placeholder="Enter Registered Phone.No" required/>
				<br/><br/>
				<input type="text" value={this.state.user_otp} onChange={e=>{this.setState({user_otp : e.target.value})}} placeholder="Enter The OTP Received" required/>
				<br/><br/>
			</div>
			<br/><br/><br/>
			<center><h4 style={{color : '#1843f0',fontWeight : '500'}}>{this.state.submissionMessage}</h4></center>
			<br/>
			<div style={{display : "flex",float: "right",justifyContent : "center",alignItems : "center"}}>
				<div>
					<button onClick={()=>{
					axios.patch("http://login-signup-1.herokuapp.com/forget/password/send_otp",{phone:this.state.phone})
					.then(e=>{

						this.setState({system_otp : e.data.otp})
						console.log(e,this.state.system_otp);
					})}}
					className="button1" style={{fontSize : "10px"}}>Send OTP</button>
				</div>
				&emsp;&emsp;
				<div>
					<button onClick={()=>{parseInt(this.state.user_otp) === this.state.system_otp?this.setState({submissionMessage : "OTP Verified!"}):this.setState({submissionMessage : "Invalid OTP!"})}} className="button">Submit</button>
				</div>
			</div>
		</div>);
	}
 }

 export default App;
