import React,{Component} from 'react';
import './App.css';

 class App extends Component {
	 constructor(props) {
		 super(props);
		 this.state = { 

			username : "",
			password : "",
			email : "",
			age : "",
			login : true

		  }
	 }
	 render() { 
		 return ( this.state.login?<div className="div1">

			 <div style={{color : "purple"}}><h1>Login</h1></div>
			 <br/><br/>
			 <div>

				 <img src="data:image/png;base64," alt="Login"/>

			 </div>
			 <br/><br/>
			 <div>

				 <input type="text" value={this.state.username} onChange={e=>{this.setState({username : e.target.value})}} required/>
				 <br/><br/>
				 <input type="password" value={this.state.password} onChange={e=>{this.setState({password : e.target.value})}} required/>
				 <br/><br/><br/>
				 &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;
				 <button onClick={()=>this.setState({username : "",password : ""})}>Done</button>
				 <br/><br/><br/><br/>
				 &emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;
				 <button onClick={()=>this.setState({login : false})} style={{backgroundColor : "#f74d52"}}>SignUp</button>

			 </div>
		 </div>:<div className="div1">

			<div style={{color : "purple"}}><h1>SignUp</h1></div>
			<br/><br/>
			<div>

				<img src="https://image.shutterstock.com/image-vector/black-simple-finger-presses-on-260nw-618096680.jpg" alt="SignUp"/>

			</div>
			<br/><br/>
			<div>

				<input type="email" value={this.state.email} onChange={e=>{this.setState({email : e.target.value})}} required/>
				<br/><br/>
				<input type="text" value={this.state.age} onChange={e=>{this.setState({age : e.target.value})}} required/>
				<br/><br/>
				<input type="text" value={this.state.value} onChange={e=>{this.setState({username : e.target.value})}} required/>
				<br/><br/>
				<input type="password" value={this.state.password} onChange={e=>{this.setState({password : e.target.value})}} required/>
				<br/><br/>
				&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;
				<button onClick={()=>this.setState({username : "",password : ""})}>Done</button>
				<br/><br/>
				&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;
				<button onClick={()=>this.setState({login : false})} style={{backgroundColor : "#f74d52"}}>Login</button>

			</div>
			</div> 
		);
	}
 }

 export default App;
